import React from 'react';
import { connect } from 'react-redux';
import { actualOrderCards, correctOrderCards, numCorrectReset } from '../actions/actions.js';
import { createCorrectOrder, createActualOrder } from '../initialState';

class Form extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            value: "1"
        }
    }

    handleChange = (e) => {

    }

    render(){
        return (
            <div>
                <form
                onSubmit={e => {
                    e.preventDefault();
                    let suits = ["Spades"];
                    
                    dispatch(correctOrderCards(createCorrectOrder(suits)));
                    dispatch(actualOrderCards(createActualOrder(suits)));
                    dispatch(numCorrectReset());
                }}
                >
                    <label>
                        <input
                        type="radio"
                        name="suits"
                        value = "1"
                        checked={this.state.value === "1"}
                        onChange={(e) => this.handleChange(e)}
                        />
                        1 Suit
                    </label>
                    <label>
                        <input
                        type="radio"
                        name="suits"
                        value = "2"
                        checked={this.state.value === "2"}
                        onChange={(e) => this.handleChange(e)}
                        />
                        1 Suit
                    </label>
                    <label>
                        <input
                        type="radio"
                        name="suits"
                        value = "3"
                        checked={this.state.value === "3"}
                        onChange={(e) => this.handleChange(e)}
                        />
                        1 Suit
                    </label>
                    <label>
                        <input
                        type="radio"
                        name="suits"
                        value = "4"
                        checked={this.state.value === "4"}
                        onChange={(e) => this.handleChange(e)}
                        />
                        1 Suit
                    </label>
                    <input
                    type="submit"
                    value="New Game"
                    >
                    </input>
                </form>
            </div>
        )
    }
}

export default connect()(Form);
